mapper.update:
UPDATE DuoComps
SET win=#{win},
    lose=#{lose}
WHERE A_main_hand_id = #{A_main_hand_id}
  AND B_main_hand_id = #{B_main_hand_id}

mapper.select:
SELECT *
FROM DuoComps
WHERE A_main_hand_id = #{A_main_hand_id}
  AND B_main_hand_id = #{B_main_hand_id}

mapper.update:
UPDATE FiveComps
SET win=#{win},
    lose=#{lose}
WHERE A_main_hand_id = #{A_main_hand_id}
  AND B_main_hand_id = #{B_main_hand_id}
  AND C_main_hand_id = #{C_main_hand_id}
  AND D_main_hand_id = #{D_main_hand_id}
  AND E_main_hand_id = #{E_main_hand_id}

mapper.select:
SELECT *
FROM FiveComps
WHERE A_main_hand_id = #{A_main_hand_id}
  AND B_main_hand_id = #{B_main_hand_id}
  AND C_main_hand_id = #{C_main_hand_id}
  AND D_main_hand_id = #{D_main_hand_id}
  AND E_main_hand_id = #{E_main_hand_id}

